mapper.select:
select e.equipment_id, e.equipment_no, e.equipment_name, e.charging_station_id, e.active, e.equipment_type, e.agreement_id, e.manufacturer, e.manufacturer_dttm, e.delivery_dttm, e.create_user, e.create_dttm, e.update_user, e.update_dttm,a.agreement_name AS agreementName ,c.charging_station_name AS chargingStatioNname,amm.ammeter_name AS ammeterName,
charing.charging_connector_name AS chargingConnectorName, equType.equipment_type_name AS equipmentTypeName,e.voltage_upper_limits,e.voltage_lower_limits,e.current,e.power,e.park_no,e.national_standard, e.device_id,e.charging_gun_no from equipment e LEFT JOIN agreement a on a.agreement_id = e.agreement_id LEFT JOIN chargingstation c ON c.charging_station_id = e.charging_station_id LEFT JOIN ammeter amm ON amm.ammeter_id = e.ammeter_id LEFT JOIN equipment_type equType ON equType.equipment_type = e.equipment_type LEFT JOIN charing_connector_type charing ON charing.charging_connector_type = e.connector_type
order by ${orderByClause}
